import React from 'react';
import './UserProgress.css';
import Chart from "chart.js/auto";
import { CategoryScale } from "chart.js";
import { useState } from "react";
import { Bar } from "react-chartjs-2";
import { Link } from 'react-router-dom';





function UserProgress() {

  const hourSpentOverAWeek = {
    labels: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
    datasets: [
      {
        label: 'Hours (hrs)',
        data: [8, 2, 5, 1, 6],
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  const totalTasks = 8;
  const completedTasks = 4;
  const taskCompletionPercentage = `${Math.round((4 / 8) * 100)}`;
 

  // Line chart data for "Achievement Summary"
  const achievementSummaryData = {
    labels: ['Week 1', 'Week 2', 'Week 3', 'Week 4', 'Week 5'],
    datasets: [
      {
        label: 'Achievements Unlocked',
        data: [1, 3, 5, 7, 10],
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 2,
        fill: true,
      },
    ],
  };


  return (
    <div>
       <h1 className='ad-user-pages-title'>Progress</h1>
      <div className='ad-user-progress-wrap'>
      <h3 className='ad-user-progress-heading'><i class="fa-solid fa-calendar-week"></i> Overall Performance</h3>
        <div className='ad-progress-performance'>
        
          <Bar
        data={hourSpentOverAWeek}
        options={{
          plugins: {
            title: {
              display: true,
              text: "Hours Spent Over A Week"
            },
            legend: {
              display: true
            }
          }
        }}
      />
        </div>
        <h3 className='ad-user-progress-heading'><i class="fa-solid fa-thumbtack"></i> Task Completion</h3>
        <div className='ad-progress-performance'>
          
          <p>
            You have completed <strong>{completedTasks}</strong> out of <strong>{totalTasks}</strong> tasks!
          </p>
          <div className="ad-progress-bar-container">
            <div
              className="ad-progress-bar-fill"
              style={{
                width: `${taskCompletionPercentage}%`,
                backgroundColor: taskCompletionPercentage < 50 ? "hsl(0, 76%, 69%)" : taskCompletionPercentage < 75 ? "hsla(54, 76%, 69%, 0.596)" : "hsla(103, 76%, 69%, 0.596)",
              }}
            ></div>
          </div>
          <div className=''>
          <p>{taskCompletionPercentage}% completed</p>
          <Link> hi</Link>
          </div>
        </div>
        <div className='ad-progress-goal'>
          <h3>Upcoming Goals</h3>
          <p>Reach 90% completion in all courses!</p>
        </div>
      </div>
    </div>
  )
}

export default UserProgress
