import React from 'react';
import './MyLearning.css';
import { useState } from 'react';
import '../../CourseDetails/Courses';
import { Link } from 'react-router-dom';
import courseImage5 from '../../../../ImageVideo/courseImage5.jpg'
import courseImage4 from '../../../../ImageVideo/courseImage4.jpg'

function MyLearning() {

  // State to manage which course list is shown
  const [isCompleted, setIsCompleted] = useState(true);

  // Function to toggle between completed and in-progress courses
  const toggleCourses = (status) => {
    setIsCompleted(status);
  };


  return (
    <div>
      <div className='ad-my-learning-wrap'>


      <div className="ad-my-learning-btn-container">
        <button
          className="ad-course-btn"
          onClick={() => toggleCourses(true)}
        >
          Completed 
        </button>
        <button
          className="ad-course-btn"
          onClick={() => toggleCourses(false)}
        >
          In Progress 
        </button>
      </div>

      {/* Course List */}
      <div className="ad-learning-course-list">
        <h2>{isCompleted ? "Completed Courses" : "In Progress Courses"}</h2>
        <ul>
          {(isCompleted ? completedCourses : inProgressCourses).map((course) => (




            <li key={course}>
              <div className="ad-course-card" >
                        <div className="ad-course-image-wrap">
                            <img src={course.image} alt={course.title} className="ad-course-image" />
                            <span className="course-category">{course.category}</span>
                        </div>
                        <div className="course-content">
                            <p className="course-author">{course.author}</p>
                            <h3 className="ad-course-title-outside">{course.title}</h3>
                            <div className="course-meta">
                                <span><i className="fas fa-clock"></i> {course.duration}</span>
                                <span><i className="fas fa-users"></i> {course.students} Students</span>
                                <span><i className="fas fa-signal"></i> {course.level}</span>
                                <span><i className="fas fa-book"></i> {course.lessons} Lessons</span>
                            </div>
                            <div className="course-footer">
                            
                                <button className="view-more-btn">
                                     <Link to={`/course-detail/${course.id}`} state={{ course }}  style={{ textDecoration: 'none', color:'white' }}>
                                        View More
                                     </Link>
                                </button>
                            </div>
                        </div>
                    </div>
            </li>





          ))}
        </ul>
      </div>


      </div>

    </div>
  )
}


const completedCourses = [
  {
            image: courseImage5,
            category: 'Design',
            title: 'Learn Web Design',
            author: 'Gemma Nguyen',
            duration: '4 Weeks',
            students: 26,
            level: 'Advanced',
            lessons: 15,
  },
  {
    image: courseImage4,
    category: 'Technology',
    title: 'Introduction to Technology',
    duration: '3 Weeks',
    author: 'Gemma Nguyen',
    students: 45,
    level: 'Beginner',
    lessons: 9,
},
];

const inProgressCourses = [
  "Course 4: Advanced Web Development",
  "Course 5: Machine Learning Fundamentals"
];

export default MyLearning
